import '../config/config.dart';class CommentModel {  late String userName;  late String userProfileImage;  late String comment;  late String commentDateTime;  CommentModel({    required this.userName,    required this.userProfileImage,    required this.comment,    required this.commentDateTime,  });  CommentModel.fromJSON(Map<String, dynamic> json) {    userName = json[TrainingApp.commentUserName]!;    userProfileImage = json[TrainingApp.userProfileImage]!;    comment = json[TrainingApp.comment]!;    commentDateTime = json[TrainingApp.commentDateTime]!;  }  Map<String, dynamic> toMap() {    return {      TrainingApp.commentUserName: userName,      TrainingApp.userProfileImage: userProfileImage,      TrainingApp.comment: comment,      TrainingApp.commentDateTime: commentDateTime,    };  }  @override  String toString() {    return '[userName:$userName,userProfileImage:$userProfileImage,comment:$comment,commentDateTime:$commentDateTime]';  }}